import React, { useState, useMemo } from 'react';
import { parsePDFWorldFamous } from '../parsers/worldFamousParser';
import { parsePDFEternal } from '../parsers/eternalParser';
import {
  Box,
  Container,
  Paper,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
  Alert,
  CircularProgress,
  TablePagination,
  TextField,
  InputAdornment,
  IconButton,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Card,
  CardContent,
  Grid,
  Fade,
  Zoom,
  Stack,
} from '@mui/material';
import {
  Search as SearchIcon,
  FileUpload as FileUploadIcon,
  Clear as ClearIcon,
  Description as DescriptionIcon,
  Numbers as NumbersIcon,
  CloudUpload as CloudUploadIcon,
} from '@mui/icons-material';

const standardizeSizeFormat = (size) => {
  if (!size) return '';
  size = size.toLowerCase().replace(/\s+/g, '');
  return size.includes('mm') ? size : `${size.match(/([\d.]+)(oz)?/)?.[1] || size}oz`;
};

const InvoiceViewer = ({ type, title }) => {
  const [invoiceData, setInvoiceData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchField, setSearchField] = useState('all');
  const [totalItems, setTotalItems] = useState(0);
  const [totalQuantity, setTotalQuantity] = useState(0);

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setLoading(true);
    setError(null);

    try {
      const buffer = await file.arrayBuffer();
      const parser = type === 'worldFamous' ? parsePDFWorldFamous : parsePDFEternal;
      const results = await parser(buffer);

      const processedResults = results.map(item => ({
        ...item,
        size: standardizeSizeFormat(item.size)
      }));

      setInvoiceData(processedResults);
      setTotalItems(processedResults.length);
      setTotalQuantity(processedResults.reduce((sum, item) => sum + (item.quantity || 0), 0));
    } catch (err) {
      setError(`Error parsing ${type} invoice: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
    setPage(0);
  };

  const handleSearchFieldChange = (event) => {
    setSearchField(event.target.value);
    setPage(0);
  };

  const clearSearch = () => {
    setSearchTerm('');
    setPage(0);
  };

  const filteredData = useMemo(() => {
    return invoiceData.filter((item) => {
      const searchLower = searchTerm.toLowerCase();

      if (searchField === 'all') {
        return Object.values(item).some(value => 
          value?.toString().toLowerCase().includes(searchLower)
        );
      }

      return item[searchField]?.toString().toLowerCase().includes(searchLower);
    });
  }, [invoiceData, searchTerm, searchField]);

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Fade in timeout={500}>
        <Paper elevation={3} sx={{ p: 4, mb: 4 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 4 }}>
            <Box>
              <Typography variant="h4" component="h1" gutterBottom>
                {title}
              </Typography>
              <Typography variant="subtitle1" color="text.secondary">
                Upload and manage your {type === 'worldFamous' ? 'World Famous' : 'Eternal'} inventory
              </Typography>
            </Box>

            <Stack direction="row" spacing={2}>
              <Button
                variant="contained"
                component="label"
                startIcon={<CloudUploadIcon />}
                disabled={loading}
                sx={{ 
                  minWidth: 180,
                  backgroundColor: type === 'worldFamous' ? '#1e40af' : '#5b21b6',
                  '&:hover': {
                    backgroundColor: type === 'worldFamous' ? '#1e3a8a' : '#4c1d95',
                  }
                }}
              >
                Upload Invoice
                <input
                  type="file"
                  hidden
                  accept=".pdf"
                  onChange={handleFileUpload}
                />
              </Button>
            </Stack>
          </Box>

          {loading && (
            <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
              <CircularProgress size={60} thickness={4} />
            </Box>
          )}

          {error && (
            <Alert severity="error" sx={{ mb: 3 }}>
              {error}
            </Alert>
          )}

          {invoiceData.length > 0 && (
            <>
              <Grid container spacing={3} sx={{ mb: 4 }}>
                <Grid item xs={12} md={6}>
                  <Card elevation={0} sx={{ bgcolor: 'background.paper' }}>
                    <CardContent>
                      <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                        <DescriptionIcon color="primary" sx={{ mr: 1 }} />
                        <Typography variant="subtitle1" color="text.secondary">
                          Total Items
                        </Typography>
                      </Box>
                      <Typography variant="h4" color="primary">
                        {totalItems}
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
                <Grid item xs={12} md={6}>
                  <Card elevation={0} sx={{ bgcolor: 'background.paper' }}>
                    <CardContent>
                      <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                        <NumbersIcon color="primary" sx={{ mr: 1 }} />
                        <Typography variant="subtitle1" color="text.secondary">
                          Total Quantity
                        </Typography>
                      </Box>
                      <Typography variant="h4" color="primary">
                        {totalQuantity}
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
              </Grid>

              <Box sx={{ display: 'flex', gap: 2, alignItems: 'center', mb: 3 }}>
                <FormControl size="small" sx={{ minWidth: 150 }}>
                  <InputLabel>Search in</InputLabel>
                  <Select
                    value={searchField}
                    onChange={handleSearchFieldChange}
                    label="Search in"
                  >
                    <MenuItem value="all">All Fields</MenuItem>
                    <MenuItem value="color">Color</MenuItem>
                    <MenuItem value="itemCode">Item Code</MenuItem>
                    <MenuItem value="size">Size</MenuItem>
                  </Select>
                </FormControl>
                <TextField
                  size="small"
                  placeholder={`Search ${searchField === 'all' ? 'all fields' : searchField}...`}
                  value={searchTerm}
                  onChange={handleSearchChange}
                  InputProps={{
                    startAdornment: (
                      <InputAdornment position="start">
                        <SearchIcon />
                      </InputAdornment>
                    ),
                    endAdornment: searchTerm && (
                      <InputAdornment position="end">
                        <IconButton size="small" onClick={clearSearch}>
                          <ClearIcon />
                        </IconButton>
                      </InputAdornment>
                    ),
                  }}
                  sx={{ width: 300 }}
                />
              </Box>

              <TableContainer component={Paper} elevation={0} sx={{ border: '1px solid', borderColor: 'divider' }}>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Item Code</TableCell>
                      <TableCell>Color</TableCell>
                      <TableCell align="right">Quantity</TableCell>
                      <TableCell>Size</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {filteredData
                      .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                      .map((item, index) => (
                        <TableRow key={index} hover>
                          <TableCell>{item.itemCode}</TableCell>
                          <TableCell>{item.color}</TableCell>
                          <TableCell align="right">{item.quantity}</TableCell>
                          <TableCell>{item.size}</TableCell>
                        </TableRow>
                      ))}
                  </TableBody>
                </Table>
              </TableContainer>

              <TablePagination
                rowsPerPageOptions={[10, 25, 50, 100]}
                component="div"
                count={filteredData.length}
                rowsPerPage={rowsPerPage}
                page={page}
                onPageChange={handleChangePage}
                onRowsPerPageChange={handleChangeRowsPerPage}
                sx={{ mt: 2 }}
              />
            </>
          )}
        </Paper>
      </Fade>
    </Container>
  );
};

export default InvoiceViewer;
