// src/parsers/eternalParser.js
import * as pdfjsLib from "pdfjs-dist/build/pdf";
import pdfWorker from "pdfjs-dist/build/pdf.worker.entry";

// Ensure worker is properly configured
pdfjsLib.GlobalWorkerOptions.workerSrc = pdfWorker;

/**
 * parsePDFEternal(arrayBuffer)
 *  For Eternal Ink style invoices
 */
export async function parsePDFEternal(arrayBuffer) {
  try {
    const pdf = await pdfjsLib.getDocument({ data: arrayBuffer }).promise;
    let allText = "";

    for (let i = 1; i <= pdf.numPages; i++) {
      const page = await pdf.getPage(i);
      const content = await page.getTextContent();
      const pageText = content.items.map((item) => item.str).join(" ");
      allText += pageText + "\n";
      console.log(`Page ${i} text:`, pageText); // Debug logging
    }

    // Find the start of the items section
    const itemsStart = allText.indexOf("Item   Description   Ordered   Rate   Amount");
    if (itemsStart === -1) {
      console.error('Could not find items section in PDF');
      throw new Error('Could not find items section in PDF');
    }

    // Extract just the items section
    const itemsText = allText.substring(itemsStart);
    console.log('Items section:', itemsText); // Debug logging

    // Split into lines and filter out empty lines
    const lines = itemsText.split('\n').filter(line => line.trim());
    
    const results = [];
    let currentItemCode = null;

    for (const line of lines) {
      // Skip header line
      if (line.includes("Item   Description   Ordered   Rate   Amount")) continue;

      // Try to find a valid item code at the start of the line
      const itemCodeMatch = line.match(/^([A-Z0-9]+(?:-[0-9]+(?:NB)?)?)\s+/);
      if (itemCodeMatch) {
        currentItemCode = itemCodeMatch[1];
      }

      // If we have a valid item code, try to parse the rest of the line
      if (currentItemCode) {
        // Extract quantity (look for a number after the description)
        const quantityMatch = line.match(/\s+(\d+)\s+[\d.,]+\s+[\d.,]+$/);
        if (quantityMatch) {
          const quantity = parseInt(quantityMatch[1], 10);
          
          // Extract description (everything between item code and quantity)
          const description = line
            .substring(currentItemCode.length)
            .replace(/\s+\d+\s+[\d.,]+\s+[\d.,]+$/, '')
            .trim();

          // Extract size from description
          let size = "1 ounce"; // default
          if (description.includes("1 ounce Bottle")) {
            size = "1 ounce";
          } else if (description.includes("1\/2 ounce")) {
            size = "1/2 ounce";
          } else if (description.includes("4 ounce")) {
            size = "4 ounce";
          } else if (description.includes("2 ounce")) {
            size = "2 ounce";
          }

          // Clean up color name
          const color = description
            .replace(/-1 ounce Bottle/i, "")
            .replace(/-1\/2 ounce/i, "")
            .replace(/-4 ounce/i, "")
            .replace(/-2 ounce/i, "")
            .replace(/\s*\([^)]*\)/g, "") // Remove any parenthetical notes
            .trim();

          // Only add if we have a valid color name (not just "ounce Bottle" etc.)
          if (color && !color.match(/^(ounce|Bottle|Bottles)$/i)) {
            results.push({
              itemCode: currentItemCode,
              color,
              quantity,
              size
            });
          }
        }
      }
    }

    if (results.length === 0) {
      console.error('No items could be parsed from the PDF');
      throw new Error('No items could be parsed from the PDF');
    }

    console.log('Parsed results:', results); // Debug logging
    return results;
  } catch (error) {
    console.error('Error parsing PDF:', error);
    throw error;
  }
}
